package com.easy.datasource.config;import cn.dev33.satoken.stp.StpUtil;import com.baomidou.mybatisplus.core.handlers.MetaObjectHandler;import com.easy.utils.lang.DateUtil;import org.apache.ibatis.reflection.MetaObject;import org.springframework.stereotype.Component;/** * mybatis plus字段自动填充拦截 * </p> * * @author Matt */@Componentpublic class MybatisPlusMetaObjectHandler implements MetaObjectHandler {    /**     * 创建     *     * @param metaObject 拦截     */    @Override    public void insertFill(MetaObject metaObject) {        setFieldValByName("createTime", DateUtil.now(), metaObject);        setFieldValByName("updateTime", DateUtil.now(), metaObject);        setFieldValByName("createBy", StpUtil.getLoginId(), metaObject);    }    /**     * 最后一次更新     *     * @param metaObject 拦截     */    @Override    public void updateFill(MetaObject metaObject) {        setFieldValByName("updateTime", DateUtil.now(), metaObject);        setFieldValByName("updateBy", StpUtil.getLoginId(), metaObject);    }}